import {useGetFriendsQuery} from "../../services/api/friends/friendsApi";
import {useSelector} from "react-redux";
import {Box, Button, Stack, Typography} from "@mui/material";
import {useNavigate} from "react-router-dom";

const Overview = () => {
    const userId = useSelector(state => state.auth.user.id);
    const {data: friends, error, isLoading} = useGetFriendsQuery(userId);
    const navigate = useNavigate();

    if (isLoading) return <p>Načítání...</p>;
    if (error) return <p>Chyba při načítání přátel</p>;

    return (
        <Stack>
            <Button onClick={() => navigate("/addfriend")}>Add</Button>
            {JSON.stringify(friends)}
            <Stack spacing={1}>
                {
                    friends.map(friend => {
                        return <FriendComponent friend={friend} key={friend.id}/>;
                    })
                }
            </Stack>
        </Stack>
    )
};

export default Overview;

const FriendComponent = ({friend}) => {
    return (
        <Stack bgcolor={"gray"} p={2}>
            <Stack direction="row">
                <Typography>{friend.name}</Typography>
                <Typography>{friend.birthday}</Typography>

            </Stack>
            <Typography>{friend.score}</Typography>
            <ScoreBar score={friend.score}/>
        </Stack>
    )
}

const ScoreBar = ({score}) => {
    return (
        <Stack sx={{width: "100%", backgroundColor: 'red', height: '50px'}}
               justifyContent="center"
               alignItems="flex-start"
               border={"2px solid black"}
               borderRadius={2}
        >
            <Box sx={{
                width: "90%",
                backgroundColor: 'white',
                height: '50px',
                borderBottomLeftRadius: 1.5,
                borderTopLeftRadius: 1.5
            }}/>
        </Stack>
    )
}
