import {
    Alert,
    Button,
    Card,
    FormControlLabel,
    Radio,
    RadioGroup,
    Select,
    Stack,
    TextField,
    Typography
} from "@mui/material";
import React from "react";
import useForm from "../../hooks/useForm";
import {useNavigate} from "react-router-dom";
import {DatePicker} from "@mui/x-date-pickers";
import dayjs from "dayjs";
import {useAddFriendMutation} from "../../services/api/friends/friendsApi";
import {useSelector} from "react-redux";

export const AddFriend = () => {
    const {formData, handleChange} = useForm({name: "", birthday: ""});
    const userId = useSelector(state => state.auth.user.id);
    const [addFriend, {isLoading, error}] = useAddFriendMutation();

    const navigate = useNavigate();

    const handleDateChange = (newValue) => {
        handleChange({
            target: {
                name: "birthday",
                value: newValue ? dayjs(newValue).format("YYYY-MM-DD") : "",
            },
        });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            await addFriend(formData).unwrap();
            navigate("/overview");
        } catch (err) {
            console.error("Login error:", err);
        }
    };

    return (
        <Stack spacing={2} maxWidth={500} component={Card} p={1}>
            <TextField
                label="Jméno"
                name="name"
                value={formData.name}
                onChange={handleChange}
            />
            <DatePicker
                label="Datum narozenin"
                value={formData.birthday ? dayjs(formData.birthday) : null}
                onChange={handleDateChange}
            />

            <Typography variant="body1" color="textSecondary">Priorita udržení kontaktu</Typography>

            <RadioGroup defaultValue="STAY_CLOSE" sx={{ gap: "12px" }}>
                <FormControlLabel value="STAY_CLOSE" control={<Radio/>} label={<RadioButtonLabel label="Neztratit kontakt" description={"Tento přítel je pro mě důležitý, chci zůstat v blízkém kontaktu."}/>} />
                <FormControlLabel value="KEEP_IN_TOUCH" control={<Radio/>} label={<RadioButtonLabel label={"Udržovat přehled"} description={"Stačí občas vědět, co se děje."}/>}/>
                <FormControlLabel value="LET IT FLOW" control={<Radio/>} label={<RadioButtonLabel label={"Nechat věci plynout"} description={"Nechávám to volně, uvidíme, kam to povede."}/>}/>
            </RadioGroup>

            <Typography variant="body1">Sledovat i skore lasky</Typography>

            {error && <Alert severity="error">{error?.data}</Alert>}
            <Button variant="contained" onClick={handleSubmit} disabled={isLoading}>Add</Button>
            <Button variant="contained" onClick={() => navigate("/overview")}>Back</Button>
        </Stack>
    );
};

const RadioButtonLabel = ({ label, description }) => {
    return (
        <Stack>
            <Typography>{label}</Typography>
            <Typography variant={"body2"} color="textSecondary">{description}</Typography>
        </Stack>
    )

}
