import {useGetFriendsQuery, useIncreaseScoreMutation} from "../../services/api/friends/friendsApi";
import {useSelector} from "react-redux";
import {Box, Dialog, IconButton, Stack, Typography} from "@mui/material";
import {useState} from "react";
import CallIcon from '@mui/icons-material/Call';
import MessageIcon from '@mui/icons-material/Message';
import VisibilityIcon from '@mui/icons-material/Visibility';
import {FriendComponent} from "./components/FriendComponent";

const Overview = () => {
    const userId = useSelector(state => state.auth.user.id);
    const {data: friends, error, isLoading} = useGetFriendsQuery(userId);
    const [modal, setModal] = useState({open: false, data: null});

    const closeModal = () => {
        setModal({open: false, data: null});
    };

    const openModal = (data) => {
        setModal({open: true, data});
    };

    if (isLoading) return <p>Načítání...</p>;
    if (error) return <p>Chyba při načítání přátel</p>;

    return (
        <div>
            <Stack>
                <Stack>
                    {
                        friends.map((friend, index) => {
                            return (
                                <Box key={index}>
                                    <FriendComponent friend={friend} openModal={openModal}/>
                                </Box>
                            )
                        })
                    }
                </Stack>
            </Stack>
            <Dialog open={modal.open} onClose={closeModal} maxWidth={"md"}>
                <Stack p={2} alignItems="center" justifyContent="center" spacing={2}>
                    <Typography variant={"h5"}>Interakce</Typography>
                    <Stack p={1} direction="row" spacing={3}>
                        <InteractionItem icon={MessageIcon} label="Message"/>
                        <InteractionItem icon={CallIcon} label="Call"/>
                        <InteractionItem icon={VisibilityIcon} label="Hang Out"/>
                    </Stack>
                </Stack>
            </Dialog>
        </div>
    )
};

export default Overview;


const InteractionItem = ({icon: IconComponent, label, friend, points}) => {
    const [increaseScore, {isLoading, isError, data}] = useIncreaseScoreMutation("", "");

    /*
    const handleIncreaseScore = async () => {
        try {
            const result = await increaseScore({friendId, points}).unwrap();  // unwrap získá výsledek bez potřeby try-catch
            console.log('Score updated successfully:', result);
        } catch (error) {
            console.error('Failed to update score:', error);
        }
    };

     */

    return (
        <Stack alignItems="center" spacing={1}>
            <IconButton sx={{backgroundColor: "#efefef"}} /*onClick={handleIncreaseScore} */>
                <IconComponent fontSize="large"/>
            </IconButton>
            <Typography>{label}</Typography>
        </Stack>
    );
};
