import {createApi} from "@reduxjs/toolkit/query/react";
import {customBaseQuery} from "../../configuration";

export const friendsApi = createApi({
    reducerPath: "friendsApi",
    baseQuery: customBaseQuery,
    endpoints: (builder) => ({

        getFriends: builder.query({
            query: (userId) => `friends/${userId}`,
        }),

        addFriend: builder.mutation({
            query: ({userId, friendData}) => ({
                url: `friends/${userId}/add`,
                method: "POST",
                body: friendData,
            }),
        }),

        increaseScore: builder.mutation({
            query: ({friendId, points}) => ({
                url: `friends/${friendId}/increase-score?points=${points}`,
                method: "PUT",
            }),
        }),

        decreaseScore: builder.mutation({
            query: ({friendId, points}) => ({
                url: `friends/${friendId}/decrease-score?points=${points}`,
                method: "PUT",
            }),
        }),

        deleteFriend: builder.mutation({
            query: (friendId) => ({
                url: `friends/${friendId}`,
                method: "DELETE",
            }),
        }),
    }),
});

export const {
    useGetFriendsQuery,
    useAddFriendMutation,
    useIncreaseScoreMutation,
    useDecreaseScoreMutation,
    useDeleteFriendMutation,
} = friendsApi;
