import {useGetFriendsQuery} from "../../services/api/friends/friendsApi";
import {useSelector} from "react-redux";
import {Avatar, Box, Button, Card, Divider, Stack, Typography} from "@mui/material";
import {useNavigate} from "react-router-dom";
import CakeIcon from '@mui/icons-material/Cake';

const Overview = () => {
    const userId = useSelector(state => state.auth.user.id);
    const {data: friends, error, isLoading} = useGetFriendsQuery(userId);
    const navigate = useNavigate();

    if (isLoading) return <p>Načítání...</p>;
    if (error) return <p>Chyba při načítání přátel</p>;
//<Button onClick={() => navigate("/addfriend")}>Add</Button>
    return (
        <Stack>

            <Stack>
                {
                    friends.map(friend => {
                        return (
                            <Box>
                                <FriendComponent friend={friend} key={friend.id}/>
                            </Box>
                        )
                    })
                }
            </Stack>
        </Stack>
    )
};

export default Overview;

const FriendComponent = ({friend}) => {
    return (
        <Stack  p={2} spacing={4} direction={"row"} alignItems={"center"} borderBottom={"1px solid lightgray"}
               >
            <Avatar/>

            <Stack flex={1} spacing={0.5}>

                <Stack direction="row" justifyContent="space-between">
                    <Typography>{friend.name} </Typography>
                    <Stack direction="row" spacing={1} alignItems="center">
                        <CakeIcon fontSize={"small"} sx={{color: "gray"}}/>
                        <Typography sx={{color: "gray"}}>{friend.birthday}</Typography>
                    </Stack>

                </Stack>
                <ScoreBar score={friend.score}/>
            </Stack>
        </Stack>
    )
}

const ScoreBar = ({score}) => {
    const height = 10
    const percentage = calculatePercentage(score, 100)

    function calculatePercentage(part, whole) {
        if (whole === 0) {
            console.error("Cannot divide by zero")
            return 0
        }
        return (part / whole) * 100;
    }

    return (
        <Stack sx={{width: "100%", height: {height}}}
               justifyContent="center"
               alignItems="flex-start"
               border={"1px solid black"}
               borderRadius={2}
        >
            <Box sx={{
                width: `${percentage}%`,
                backgroundColor: 'white',
                height: '50px',
                borderRadius: 1.5
            }}/>
        </Stack>
    )
}
