import { Stack, Box } from "@mui/material";

export const AppLayout = ({ children }) => {
    return (
        <Stack height="100vh">
            {/* Horní část, může být třeba navigace */}
            <Box>
                top
            </Box>

            {/* Hlavní obsah, který se roztáhne na zbývající místo */}
            <Box sx={{ flexGrow: 1, overflow: "auto" }}>
                {children}
            </Box>

            {/* Dolní část, např. footer */}
            <Box>
                bottom
            </Box>
        </Stack>
    );
};
