import {useGetFriendsQuery} from "../../services/api/friends/friendsApi";
import {useSelector} from "react-redux";
import {Avatar, Box, Button, Card, Stack, Typography} from "@mui/material";
import {useNavigate} from "react-router-dom";
import CakeIcon from '@mui/icons-material/Cake';

const Overview = () => {
    const userId = useSelector(state => state.auth.user.id);
    const {data: friends, error, isLoading} = useGetFriendsQuery(userId);
    const navigate = useNavigate();

    if (isLoading) return <p>Načítání...</p>;
    if (error) return <p>Chyba při načítání přátel</p>;

    return (
        <Stack>
            <Button onClick={() => navigate("/addfriend")}>Add</Button>
            <Stack spacing={2}>
                {
                    friends.map(friend => {
                        return <FriendComponent friend={friend} key={friend.id}/>;
                    })
                }
            </Stack>
        </Stack>
    )
};

export default Overview;

const FriendComponent = ({friend}) => {
    return (
        <Stack p={1} direction={"row"} justifyContent="center" spacing={1} component={Card} borderRadius={3}>
            <Avatar/>

            <Stack direction="row"  justifyContent="space-between">
                <Typography variant={"h5"}>{friend.name} </Typography>
                <Stack direction="row" spacing={1} alignItems="center">
                    <CakeIcon/>
                    <Typography>{friend.birthday}</Typography>
                </Stack>

            </Stack>
            <ScoreBar score={friend.score}/>
        </Stack>
    )
}

const ScoreBar = ({score}) => {
    const height = 20
    const percentage = calculatePercentage(score, 100)

    function calculatePercentage(part, whole) {
        if (whole === 0) {
            console.error("Cannot divide by zero")
            return 0
        }
        return (part / whole) * 100;
    }

    return (
        <Stack sx={{width: "100%", height: {height}}}
               justifyContent="center"
               alignItems="flex-start"
               border={"1px solid black"}
               borderRadius={2}
        >
            <Box sx={{
                width: `${percentage}%`,
                backgroundColor: 'white',
                height: '50px',
                borderRadius: 1.5
            }}/>
        </Stack>
    )
}
